import react, { Component } from "react";
import { Form, FormGroup, Label, Input, Button } from "reactstrap";
import Header from "../../header/Header";

export default class CadastroDeFuncionarios extends Component {
  constructor(props) {
    super(props);
    this.state = {
      message: this.props.state ? this.props.state.message : "",
    };
  }

  save = (funcionario) => {
    const url = "http://localhost:8080/dashboard";
    let data = {
      nome: funcionario.nome,
      cpf: funcionario.cpf,
      dataDeNascimento: funcionario.dataDeNascimento,
      cargo: {
        id: funcionario.cargo.id,
      },
      usuario:{
        nomeUsuario: funcionario.usuario.nomeUsuario,
        senha: funcionario.usuario.senha,
      },
    };
    const requestInfo = {
      method: "POST",
      body: JSON.stringify(data),
      headers: new Headers({
        "Content-Type": "application/json",
      }),
    };
    fetch(url, requestInfo)
      .then((response) => {
        console.log(funcionario.nome, funcionario.cargo.id, this.observation);
        return response;
      })
      .catch((e) => {
        this.setState({ message: e.message });
        console.log(this.email, this.nome, this.observacoes);
      });
  };

  render() {
    return (
      <div>
        <Header title="Cadastro de Funcionarios" />
        <hr />
        <Form>
          <FormGroup>
            <Label for="name"> Nome do Funcionario: </Label>
            <Input
              type="text"
              id="name"
              onChange={(e) => (this.name = e.target.value)}
              placeholder="Informe o seu nome"
            />
          </FormGroup>
          <FormGroup>
            <Label for="cpf"> CPF Do Funcionario: </Label>
            <Input
              type="text"
              id="cpf"
              onChange={(e) => (this.email = e.target.value)}
              placeholder="Informe o seu horário de entrada"
            />
          </FormGroup>
          <FormGroup>
            <Label for="horario"> Data De Nascimento: </Label>
            <Input
              type="date"
              id="dataDeNascimento"
              onChange={(e) => (this.observation = e.target.value)}
              placeholder="Informe o seu horário de saida"
            />
          </FormGroup>
          <FormGroup>
            <Label for="horario"> Cargo: </Label>
            <Input
              type="text"
              id="cargo"
              onChange={(e) => (this.observation = e.target.value)}
            />
          </FormGroup>
          <FormGroup>
            <Label for="name"> Nome de Usuario: </Label>
            <Input
              type="text"
              id="name"
              onChange={(e) => (this.name = e.target.value)}
              placeholder="Informe o seu nome de usuario"
            />
          </FormGroup>
          <FormGroup>
            <Label for="senha"> Senha: </Label>
            <Input
              type="password"
              id="senha"
              onChange={(e) => (this.name = e.target.value)}
              placeholder="Digite a sua Senha"
            />
          </FormGroup>
          
          <Button color="danger" block onClick={this.save}>
            ENVIAR
          </Button>
        </Form>
      </div>
    );
  }
}
